    const today = new Date().getDay();
    let newDaysOfWeek = []
    let tracker = today;
    for(let i = 0; i < 6; i++){ 
        newDaysOfWeek.push(daysOfWeek[tracker])
        if(tracker === 6){
            tracker = 0
        } else{
            tracker++
        }
    }

    const component = (
        <ul className="days-of-week">
        {newDaysOfWeek.map((day, index) => (
            <li key={index}>
                <button 
                    ref={pushRef} 
                    className={`day ${index === 0 ? ('active') : ''}`}
                        >
                    {index === 0 && ('Today')}
                    {index === 1 && ('Tomorrow')} 
                    {index > 1 && day.split('', 3)} 
                </button>
            </li>
        ))}
    </ul>
    )

    return {component, newDaysOfWeek};
}

